    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
      try{
          
        String sql= "INSERT INTO `accounts`(`Username`, `Name`, `Password`, `Sec_Q`, `Answer`) VALUES (?,?,?,?,?)";
        
        pst = connection.prepareStatement(sql);
        pst.setString(1,jTextField1.getText());
        pst.setString(2,jTextField4.getText());
        pst.setString(3,jTextField2.getText());
        pst.setString(4,(String)jComboBox1.getSelectedItem());
        pst.setString(5,jTextField3.getText());
        pst.execute();
        JOptionPane.showMessageDialog(null, "New Account Created","Successfully Log In.",3);
     
          
      }catch(Exception e)
      {
          JOptionPane.showMessageDialog(null, e);
          
      }
    }//GEN-LAST:event_jButton1ActionPerformed

